<html>
	<head>
		<title>
			<?php
				$dir = $_GET["d"] ?? '';
				$search = $_GET["s"];
				echo $search . ' - Broken Spine Search' . (($dir)?' - ' . $dir:'');
			?>
		</title>
		<link rel="icon" href="data:image/png;base64,">
		<style>
			a
			{
				color: black;
				text-decoration: none;
			}
			
			.line:nth-child(even) .file
			{
				background-color: rgba(1,1,1,.14);
			}
			<?php
				if (isset($_GET["m"]))
				{
					echo '.line{overflow: auto;} *{font-size: 5vw;}';
				}
			?>
		</style>
	</head>
	<body onload="document.getElementById('search').select();">
		<pre>
			<div class="line"><form action="search.php" autocomplete="off" style="display: inline;"><input type="hidden" name="d" value="<?php echo $dir; ?>"><input id="search" onfocus="this.select()" type="text" name="s" placeholder="Search" value="<?php echo $search; ?>" style="position: relative; width: 100%"></form></div>
			<?php
				$list = [];
				$terms = preg_split('/[^A-Z0-9]/i', preg_replace('/(?:(?:\b(?:a|an|the)\b)|[^A-Z0-9]|(?: {2,}))+/i', ' ', $search));
				function recurse($d,$l)
				{
					global $list,$terms;
					$files = scandir('.\\' . $d);
					unset($files[array_search('.', $files)]);
					unset($files[array_search('..', $files)]);
					$files = array_values($files);
					for ($i = 0; $i < count($files); $i++)
					{
						if (is_dir($d . $files[$i]))
						{
							recurse($d . $files[$i] . '/', $l+1);
						}
						else
						{
							$sort = preg_replace('/(?:(?:\b(?:a|an|the)\b)|[^a-z0-9]|(?: {2,}))+/i', ' ', $d . $files[$i]);
							$words = explode(' ', trim($sort,' '));
							$lls = false;
							$llsa = 0;
							
							for ($ii = 0; $ii < count($terms); $ii++)
							{
								for ($iii = 0; $iii < count($words); $iii++)
								{
									$cls = levenshtein(strtolower($words[$iii]),strtolower($terms[$ii]))/strlen($words[$iii]);
									
									if ($cls < $lls || $lls === false)
									{
										$lls = $cls;
									}
								}
								$llsa += $lls;
								$lls = false;
							}
							
							$llsa = $llsa/count($terms);
							$list[] = (object) array('file' => $files[$i], 'dir' => $d, 'sort' => $sort, 'ls' => $llsa);
						}
					}
				}
				
				$dir = preg_replace('/^(.*)\/$/','$1',$dir);
				recurse((($dir)?$dir:'.') . '/',0);
				
				usort($list, function ($a, $b)
				{
					if ($a->ls !== $b->ls)
					{
						return $a->ls <=> $b->ls;
					}
					else
					{
						return strnatcasecmp($a->sort, $b->sort);
					}
				});
				
				for ($i = 0; $i < count($list); $i++)
				{
					$ext = preg_replace('/.*(?:\.(.*))/','$1',$list[$i]->file);
					switch ($ext)
					{
						case 'htaccess':
						case 'git':
						case 'gitignore':
						case 'ico':
						case 'php':
						
						break;
						default:
							echo '<div class="line"><a class="file" href="' . $list[$i]->dir . preg_replace('/\.\/(.*)/','$1',($list[$i]->file)) . '">' . $list[$i]->file . '</a></div>';
						break;
					}
				}
			?>
		</pre>
	</body>
</html>
